mapper.update:
UPDATE QUESTIONS


questions_title = #{questionsTitle},
questions_content = #{questionsContent},
questions_date = #{questionsDate}

WHERE QUESTIONS_ID = #{questionsId}

mapper.select:
SELECT COUNT(*)
FROM QUESTIONS_LIKE
WHERE QUESTIONS_ID = #{questionsId}

mapper.update:
UPDATE QUESTIONS_COMMENT

comment_date = SYSDATE,

comment_body = #{commentBody}

WHERE comment_num = #{commentNum}

UPDATE QUESTIONS
SET HIT = HIT +1
WHERE QUESTIONS_ID = #{questionsId}
